import React, { useState, useEffect } from "react";
import { Button } from "../../components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "../../components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../../components/ui/select";
import { Input } from "../../components/ui/input";
import { Label } from "../../components/ui/label";
import { Textarea } from "../../components/ui/textarea";
import { Switch } from "../../components/ui/switch";
import { Link } from "react-router-dom";
import { Sidebar, superAdminMenu } from "../../components/layout/sidebar";
import { useToast } from "../../hooks/use-toast";

export default function SuperAdminProgramManagement() {
  const { toast } = useToast();
  const [showProgramDialog, setShowProgramDialog] = useState(false);
  const [showPathDialog, setShowPathDialog] = useState(false);
  const [showCombinationDialog, setShowCombinationDialog] = useState(false);
  const [selectedProgram, setSelectedProgram] = useState(null);
  const [selectedPath, setSelectedPath] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterStatus, setFilterStatus] = useState("all");
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [programs, setPrograms] = useState([]);
  const [paths, setPaths] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    status: "active",
    capacity: "",
    fee: ""
  });
  const [pathFormData, setPathFormData] = useState({
    name: "",
    description: "",
    status: "active",
    requirements: []
  });

  // Get auth token
  const getAuthToken = () => {
    return localStorage.getItem('token');
  };

  // Fetch programs from API
  const fetchPrograms = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/programs', {
        headers: {
          'Authorization': `Bearer ${getAuthToken()}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch programs');
      }

      const result = await response.json();
      setPrograms(Array.isArray(result.data) ? result.data : []);
    } catch (error) {
      console.error('Error fetching programs:', error);
      toast({
        title: "Error",
        description: "Gagal mengambil data program",
        variant: "destructive"
      });
      setPrograms([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPrograms();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePathInputChange = (e) => {
    const { name, value } = e.target;
    setPathFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSelectChange = (name, value) => {
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePathSelectChange = (name, value) => {
    setPathFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      status: "active",
      capacity: "",
      fee: ""
    });
  };

  const resetPathForm = () => {
    setPathFormData({
      name: "",
      description: "",
      status: "active",
      requirements: []
    });
  };

  const handleAddProgram = () => {
    setSelectedProgram(null);
    resetForm();
    setShowProgramDialog(true);
  };

  const handleEditProgram = (program) => {
    setSelectedProgram(program);
    setFormData({
      name: program.name,
      description: program.description,
      status: program.status,
      capacity: typeof program.capacity === 'number' ? program.capacity.toString() : "",
      fee: typeof program.fee === 'number' ? program.fee.toString() : ""
    });
    setShowProgramDialog(true);
  };

  const handleProgramSubmit = async (e) => {
    e.preventDefault();
    
    try {
      setSubmitting(true);
      const programData = {
        name: formData.name,
        description: formData.description,
        status: formData.status,
        capacity: parseInt(formData.capacity) || 0,
        fee: parseInt(formData.fee) || 0
      };

      const url = selectedProgram 
        ? `/api/programs/${selectedProgram.program_id}`
        : '/api/programs';
      
      const method = selectedProgram ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: {
          'Authorization': `Bearer ${getAuthToken()}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(programData)
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || 'Failed to save program');
      }

      toast({
        title: "Berhasil",
        description: selectedProgram ? "Program berhasil diperbarui" : "Program berhasil dibuat",
      });

      setShowProgramDialog(false);
      setSelectedProgram(null);
      resetForm();
      fetchPrograms(); // Refresh the list
    } catch (error) {
      console.error('Error saving program:', error);
      toast({
        title: "Error",
        description: error.message || "Gagal menyimpan program",
        variant: "destructive"
      });
    } finally {
      setSubmitting(false);
    }
  };

  const handleAddPath = () => {
    setSelectedPath(null);
    resetPathForm();
    setShowPathDialog(true);
  };

  const handleEditPath = (path) => {
    setSelectedPath(path);
    setPathFormData({
      name: path.name,
      description: path.description,
      status: path.status,
      requirements: path.requirements || []
    });
    setShowPathDialog(true);
  };

  const handleAddCombination = () => {
    setShowCombinationDialog(true);
  };

  // Filter programs based on search and status
  const filteredPrograms = Array.isArray(programs) ? programs.filter(program => {
    const matchesSearch = searchQuery === "" || 
      program.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      program.description?.toLowerCase().includes(searchQuery.toLowerCase());
    
    const matchesStatus = filterStatus === "all" || program.status === filterStatus;
    
    return matchesSearch && matchesStatus;
  }) : [];

  if (loading) {
    return (
      <div className="flex min-h-screen">
        <Sidebar menu={superAdminMenu} />
        <div className="flex-1 flex flex-col">
          <div className="p-6">
            <div className="flex items-center justify-center h-64">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                <p>Memuat data program...</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen">
      <Sidebar menu={superAdminMenu} />
      <div className="flex-1 flex flex-col">
        <div className="p-6">
          <div className="mb-6 flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold">Manajemen Program & Jalur</h1>
              <p className="text-muted-foreground">Kelola program sekolah dan jalur pendaftaran</p>
            </div>
          </div>

          {/* Search and Filter */}
          <div className="mb-6 flex items-center gap-4">
            <div className="flex-1">
              <input
                type="text"
                placeholder="Cari program atau jalur..."
                className="h-10 w-full rounded-md border px-4"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger className="w-[180px]">
                <SelectValue placeholder="Filter Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Semua Status</SelectItem>
                <SelectItem value="active">Aktif</SelectItem>
                <SelectItem value="inactive">Nonaktif</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Programs Section */}
          <div className="mb-8">
            <div className="mb-4 flex items-center justify-between">
              <h2 className="text-xl font-semibold">Program Sekolah</h2>
              <Button onClick={handleAddProgram}>+ Tambah Program</Button>
            </div>
            <div className="grid gap-4 md:grid-cols-2">
              {(filteredPrograms || []).length === 0 ? (
                <div className="col-span-2 text-center py-8 text-muted-foreground">
                  Tidak ada data program
                </div>
              ) : (
                (filteredPrograms || []).map((program) => (
                  <div key={program.program_id} className="rounded-lg border p-4">
                    <div className="flex items-start justify-between">
                      <div>
                        <h3 className="font-semibold">{program.name}</h3>
                        <p className="text-sm text-muted-foreground">{program.description}</p>
                        <div className="mt-2 space-y-1">
                          <p className="text-xs text-muted-foreground">
                            Kapasitas: {program.capacity || 0} siswa
                          </p>
                          <p className="text-xs text-muted-foreground">
                            Biaya: Rp {program.fee?.toLocaleString() || 0}
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <Switch checked={program.status === "active"} />
                        <Button variant="outline" size="sm" onClick={() => handleEditProgram(program)}>
                          Edit
                        </Button>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Paths Section */}
          <div className="mb-8">
            <div className="mb-4 flex items-center justify-between">
              <h2 className="text-xl font-semibold">Jalur Pendaftaran</h2>
              <Button onClick={handleAddPath}>+ Tambah Jalur</Button>
            </div>
            <div className="grid gap-4 md:grid-cols-2">
              {(paths || []).length === 0 ? (
                <div className="col-span-2 text-center py-8 text-muted-foreground">
                  Tidak ada data jalur
                </div>
              ) : (
                (paths || []).map((path) => (
                  <div key={path.id} className="rounded-lg border p-4">
                    <div className="flex items-start justify-between">
                      <div>
                        <h3 className="font-semibold">{path.name}</h3>
                        <p className="text-sm text-muted-foreground">{path.description}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Switch checked={path.status === "active"} />
                        <Button variant="outline" size="sm" onClick={() => handleEditPath(path)}>
                          Edit
                        </Button>
                      </div>
                    </div>
                    <div className="mt-4">
                      <h4 className="text-sm font-medium mb-2">Persyaratan:</h4>
                      <ul className="list-inside list-disc text-sm text-muted-foreground">
                        {Array.isArray(path.requirements) ? path.requirements.map((req, index) => (
                          <li key={index}>{req}</li>
                        )) : null}
                      </ul>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Combination Section */}
          <div>
            <div className="mb-4 flex items-center justify-between">
              <h2 className="text-xl font-semibold">Kombinasi Program & Jalur</h2>
              <Button onClick={handleAddCombination}>+ Tambah Kombinasi</Button>
            </div>
            <div className="rounded-lg border">
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="border-b bg-muted/50">
                      <th className="p-4 text-left">Program</th>
                      <th className="p-4 text-left">Jalur Tersedia</th>
                      <th className="p-4 text-left">Status</th>
                      <th className="p-4 text-left">Aksi</th>
                    </tr>
                  </thead>
                  <tbody>
                    {(Array.isArray(programs) ? programs : []).map((program) => (
                      <tr key={program.id} className="border-b">
                        <td className="p-4">{program.name}</td>
                        <td className="p-4">
                          <div className="flex flex-wrap gap-2">
                            {Array.isArray(program.paths) ? program.paths.map((pathId) => {
                              const path = (Array.isArray(paths) ? paths : []).find((p) => p.id === pathId);
                              return path ? (
                                <span
                                  key={path.id}
                                  className="rounded-full bg-primary/10 px-2 py-1 text-xs text-primary"
                                >
                                  {path.name}
                                </span>
                              ) : null;
                            }) : null}
                          </div>
                        </td>
                        <td className="p-4">
                          <span className="rounded-full bg-green-100 px-2 py-1 text-xs text-green-800">
                            Aktif
                          </span>
                        </td>
                        <td className="p-4">
                          <Button variant="outline" size="sm">Edit</Button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Program Dialog */}
          <Dialog open={showProgramDialog} onOpenChange={setShowProgramDialog}>
            <DialogContent className="sm:max-w-[500px]">
              <DialogHeader>
                <DialogTitle>
                  {selectedProgram ? "Edit Program" : "Tambah Program Baru"}
                </DialogTitle>
              </DialogHeader>
              <form onSubmit={handleProgramSubmit}>
                <div className="grid gap-4 py-4">
                  <div className="grid gap-2">
                    <Label htmlFor="name">Nama Program</Label>
                    <Input
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Masukkan nama program"
                      required
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="description">Deskripsi</Label>
                    <Textarea
                      id="description"
                      name="description"
                      value={formData.description}
                      onChange={handleInputChange}
                      placeholder="Masukkan deskripsi program"
                      required
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="capacity">Kapasitas</Label>
                    <Input
                      id="capacity"
                      name="capacity"
                      type="number"
                      value={formData.capacity}
                      onChange={handleInputChange}
                      placeholder="Masukkan kapasitas"
                      required
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="fee">Biaya</Label>
                    <Input
                      id="fee"
                      name="fee"
                      type="number"
                      value={formData.fee}
                      onChange={handleInputChange}
                      placeholder="Masukkan biaya"
                      required
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="status">Status</Label>
                    <Select value={formData.status} onValueChange={(value) => handleSelectChange('status', value)}>
                      <SelectTrigger>
                        <SelectValue placeholder="Pilih status" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="active">Aktif</SelectItem>
                        <SelectItem value="inactive">Nonaktif</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
                <DialogFooter>
                  <Button type="button" variant="outline" onClick={() => setShowProgramDialog(false)} disabled={submitting}>
                    Batal
                  </Button>
                  <Button type="submit" disabled={submitting}>
                    {submitting ? "Menyimpan..." : "Simpan"}
                  </Button>
                </DialogFooter>
              </form>
            </DialogContent>
          </Dialog>

          {/* Path Dialog */}
          <Dialog open={showPathDialog} onOpenChange={setShowPathDialog}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>{selectedPath ? "Edit Jalur" : "Tambah Jalur"}</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="path-name">Nama Jalur</Label>
                  <Input id="path-name" defaultValue={selectedPath?.name} />
                </div>
                <div>
                  <Label htmlFor="path-description">Deskripsi</Label>
                  <Textarea id="path-description" defaultValue={selectedPath?.description} />
                </div>
                <div>
                  <Label>Persyaratan</Label>
                  <div className="space-y-2">
                    {selectedPath?.requirements.map((req, index) => (
                      <div key={index} className="flex items-center gap-2">
                        <Input defaultValue={req} />
                        <Button variant="outline" size="sm">Hapus</Button>
                      </div>
                    ))}
                    <Button variant="outline" size="sm">+ Tambah Persyaratan</Button>
                  </div>
                </div>
                <div className="flex items-center gap-2">
                  <Switch id="path-status" defaultChecked={selectedPath?.status === "active"} />
                  <Label htmlFor="path-status">Status Aktif</Label>
                </div>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setShowPathDialog(false)}>
                  Batal
                </Button>
                <Button>Simpan</Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>

          {/* Combination Dialog */}
          <Dialog open={showCombinationDialog} onOpenChange={setShowCombinationDialog}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Tambah Kombinasi Program & Jalur</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="program">Program</Label>
                  <Select>
                    <SelectTrigger>
                      <SelectValue placeholder="Pilih Program" />
                    </SelectTrigger>
                    <SelectContent>
                      {programs.map((program) => (
                        <SelectItem key={program.id} value={program.id.toString()}>
                          {program.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label>Jalur Pendaftaran</Label>
                  <div className="space-y-2">
                    {paths.map((path) => (
                      <div key={path.id} className="flex items-center gap-2">
                        <Switch id={`path-${path.id}`} />
                        <Label htmlFor={`path-${path.id}`}>{path.name}</Label>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
              <DialogFooter>
                <Button variant="outline" onClick={() => setShowCombinationDialog(false)}>
                  Batal
                </Button>
                <Button>Simpan</Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </div>
      </div>
    </div>
  );
}
