import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { useToast } from "../hooks/use-toast";

export default function StudentRegistrationPage() {
  const [student, setStudent] = useState({
    nama_lengkap: "",
    tempat_lahir: "",
    tanggal_lahir: "",
    jenis_kelamin: "L",
    alamat: ""
  });
  const [documents, setDocuments] = useState({
    akte: null,
    kk: null,
    rapor: null,
  });
  const [programId, setProgramId] = useState("");
  const [tahunAjaranId, setTahunAjaranId] = useState("");
  const [jalur, setJalur] = useState("");
  const [catatan, setCatatan] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();
  const navigate = useNavigate();
  const [tahunAjaranOptions, setTahunAjaranOptions] = useState([]);
  const [sudahAdaSiswa, setSudahAdaSiswa] = useState(false);
  const [programOptions, setProgramOptions] = useState([]);
  const [jalurOptions, setJalurOptions] = useState([]);

  const handleStudentChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const handleDocumentChange = (e) => {
    setDocuments({ ...documents, [e.target.name]: e.target.files[0] });
  };

  const getToken = () => {
    return localStorage.getItem('token') || (JSON.parse(localStorage.getItem('user'))?.token);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      // 1. Tambah siswa
      const resSiswa = await fetch("/api/siswa", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${getToken()}`
        },
        body: JSON.stringify(student),
      });
      const dataSiswa = await resSiswa.json();
      if (!resSiswa.ok || !dataSiswa.data || !dataSiswa.data.siswa_id) {
        throw new Error(dataSiswa.message || "Gagal menambah siswa");
      }
      const siswa_id = dataSiswa.data.siswa_id;

      // 2. Submit pendaftaran (dapatkan pendaftaran_id)
      const resPendaftaran = await fetch("/api/pendaftaran", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${getToken()}`
        },
        body: JSON.stringify({
          siswa_id,
          program_id: programId,
          tahun_ajaran_id: tahunAjaranId,
          catatan,
        }),
      });
      const dataPendaftaran = await resPendaftaran.json();
      if (!resPendaftaran.ok || !dataPendaftaran.data || !dataPendaftaran.data.pendaftaran_id) {
        throw new Error(dataPendaftaran.message || "Gagal mendaftar");
      }
      const pendaftaran_id = dataPendaftaran.data.pendaftaran_id;

      // 3. Upload dokumen (pakai pendaftaran_id)
      for (const [jenis, file] of Object.entries(documents)) {
        if (!file) continue;
        const formData = new FormData();
        formData.append("file", file);
        formData.append("pendaftaran_id", pendaftaran_id);
        formData.append("jenis_dokumen",
          jenis === "kk"
            ? "kartu_keluarga"
            : jenis === "akte"
            ? "akta_kelahiran"
            : "lainnya" // rapor dikirim sebagai 'lainnya'
        );
        const resDoc = await fetch("/api/dokumen", {
          method: "POST",
          headers: {
            "Authorization": `Bearer ${getToken()}`
          },
          body: formData,
        });
        const dataDoc = await resDoc.json();
        if (!resDoc.ok) throw new Error(dataDoc.message || `Gagal upload dokumen ${jenis}`);
      }

      toast({
        title: "Pendaftaran siswa berhasil",
        description: "Data siswa dan dokumen berhasil didaftarkan.",
      });
      navigate("/dashboard"); // atau ke halaman lain sesuai kebutuhan
    } catch (error) {
      toast({
        title: "Pendaftaran gagal",
        description: error.message,
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // Fetch tahun ajaran dari backend
  useEffect(() => {
    fetch('/api/tahunajaran', {
      headers: {
        'Authorization': `Bearer ${getToken()}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.data) {
          setTahunAjaranOptions(data.data);
        }
      });
  }, []);

  // Cek apakah user sudah pernah mendaftarkan siswa
  useEffect(() => {
    fetch('/api/siswa/my', {
      headers: {
        'Authorization': `Bearer ${getToken()}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.data && data.data.length > 0) {
          setSudahAdaSiswa(true);
        }
      });
  }, []);

  // Fetch program dari backend
  useEffect(() => {
    fetch('/api/program', {
      headers: {
        'Authorization': `Bearer ${getToken()}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.data) {
          setProgramOptions(data.data);
        }
      });
  }, []);

  // Fetch jalur pendaftaran dari backend
  useEffect(() => {
    fetch('/api/program/jalur', {
      headers: {
        'Authorization': `Bearer ${getToken()}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (data && data.data) {
          setJalurOptions(data.data);
        }
      });
  }, []);

  // Mapping label jalur
  const getJalurLabel = (value) => {
    switch (value) {
      case 'reguler': return 'Reguler';
      case 'Prestasi akademik': return 'Prestasi Akademik';
      case 'prestasi non akademik': return 'Prestasi Non Akademik';
      case 'tahfiz': return 'Tahfizh';
      default: return value;
    }
  };

  return (
    <div className="flex min-h-screen items-center justify-center p-6">
      {sudahAdaSiswa ? (
        <div className="w-full max-w-lg bg-white p-8 rounded-lg shadow text-center">
          <h1 className="text-2xl font-bold mb-4">Pendaftaran Siswa</h1>
          <p className="mb-4">Anda sudah mendaftarkan siswa. Satu wali hanya boleh mendaftarkan satu siswa.</p>
          <Button onClick={() => navigate('/dashboard')}>Kembali ke Dashboard</Button>
        </div>
      ) : (
        <form className="w-full max-w-lg space-y-6 bg-white p-8 rounded-lg shadow" onSubmit={handleSubmit}>
          <h1 className="text-2xl font-bold mb-4">Pendaftaran Siswa Baru</h1>
          {/* Data Siswa */}
          <div className="space-y-2">
            <Label htmlFor="nama_lengkap">Nama Lengkap Siswa</Label>
            <Input id="nama_lengkap" name="nama_lengkap" value={student.nama_lengkap} onChange={handleStudentChange} required disabled={isLoading} />
          </div>
          <div className="space-y-2">
            <Label htmlFor="tempat_lahir">Tempat Lahir</Label>
            <Input id="tempat_lahir" name="tempat_lahir" value={student.tempat_lahir} onChange={handleStudentChange} required disabled={isLoading} />
          </div>
          <div className="space-y-2">
            <Label htmlFor="tanggal_lahir">Tanggal Lahir</Label>
            <Input id="tanggal_lahir" name="tanggal_lahir" type="date" value={student.tanggal_lahir} onChange={handleStudentChange} required disabled={isLoading} />
          </div>
          <div className="space-y-2">
            <Label htmlFor="jenis_kelamin">Jenis Kelamin</Label>
            <select id="jenis_kelamin" name="jenis_kelamin" value={student.jenis_kelamin} onChange={handleStudentChange} required disabled={isLoading} className="w-full border rounded p-2">
              <option value="L">Laki-laki</option>
              <option value="P">Perempuan</option>
            </select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="alamat">Alamat</Label>
            <Input id="alamat" name="alamat" value={student.alamat} onChange={handleStudentChange} required disabled={isLoading} />
          </div>
          {/* Upload Dokumen */}
          <div className="space-y-2">
            <Label htmlFor="akte">Akte Kelahiran (PDF)</Label>
            <Input id="akte" name="akte" type="file" accept=".pdf" onChange={handleDocumentChange} required disabled={isLoading} />
          </div>
          <div className="space-y-2">
            <Label htmlFor="kk">Kartu Keluarga (PDF)</Label>
            <Input id="kk" name="kk" type="file" accept=".pdf" onChange={handleDocumentChange} required disabled={isLoading} />
          </div>
          <div className="space-y-2">
            <Label htmlFor="rapor">Rapor (PDF)</Label>
            <Input id="rapor" name="rapor" type="file" accept=".pdf" onChange={handleDocumentChange} required disabled={isLoading} />
          </div>
          {/* Pilihan Program, Jalur, Tahun Ajaran */}
          <div className="space-y-2">
            <Label htmlFor="program">Program Sekolah</Label>
            <select id="program" value={programId} onChange={e => setProgramId(e.target.value)} required disabled={isLoading} className="w-full border rounded p-2">
              <option value="">Pilih Program</option>
              {programOptions.map(opt => (
                <option key={opt.program_id} value={opt.program_id}>{opt.nama_program}</option>
              ))}
            </select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="jalur">Jalur Pendaftaran</Label>
            <select
              id="jalur"
              name="jalur"
              value={jalur}
              onChange={e => setJalur(e.target.value)}
              required
              className="w-full border rounded px-3 py-2"
            >
              <option value="">Pilih Jalur</option>
              {jalurOptions.map(jalur => (
                <option key={jalur.jenis_pendaftar + '-' + jalur.program_id} value={jalur.jenis_pendaftar}>
                  {jalur.jenis_pendaftar}
                </option>
              ))}
            </select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="tahunajaran">Tahun Ajaran</Label>
            <select id="tahunajaran" value={tahunAjaranId} onChange={e => setTahunAjaranId(e.target.value)} required disabled={isLoading} className="w-full border rounded p-2">
              <option value="">Pilih Tahun Ajaran</option>
              {tahunAjaranOptions.map(opt => (
                <option key={opt.id_tahunajaran} value={opt.id_tahunajaran}>{opt.tahun_ajaran}</option>
              ))}
            </select>
          </div>
          <div className="space-y-2">
            <Label htmlFor="catatan">Catatan (opsional)</Label>
            <Input id="catatan" value={catatan} onChange={e => setCatatan(e.target.value)} disabled={isLoading} />
          </div>
          <Button type="submit" className="w-full" disabled={isLoading}>
            {isLoading ? "Memproses..." : "Daftarkan Siswa"}
          </Button>
          <Button
            type="button"
            variant="outline"
            className="w-full"
            onClick={() => navigate('/dashboard')}
            disabled={isLoading}
          >
            Kembali
          </Button>
        </form>
      )}
    </div>
  );
}
